int maximumScore(int* nums, int numsSize, int* multipliers, int multipliersSize){
    int *dp1 = (int *)calloc(m, sizeof(int));
    int *dp2 = (int *)calloc(m, sizeof(int));
    int *tmp;
    int left;

    for(int i=m-1; i>=0; --i) { // iterate backwards through multipliers.
        for(left=i; left>=0; --left) { // iterate backwards through nums starting from length of multipliers.
            // set the new maximum for dp1[left]
            dp1[left] = MAX(
                nums[left] * multipliers[i] + dp2[left+1], //current left*multiplier + previous left+1.
                nums[right] * multipliers[i] + dp2[left] // current right*multiplier + previous maxes left.
            );
        }
        // swap
        tmp = dp1;
        dp1 = dp2;
        dp2 = tmp;
    }
    
    return dp2[0];
}

